import React from 'react';
import StripeCheckout from 'react-stripe-checkout';

const StripeCheckoutButton = ({ price }) => {
  const priceForStripe = price * 100;
  const publishableKey = 'pk_test_51H0ir0AyvULANtQRbAun6nutc7nOKY58fw2399IfpdyL4nmZCHWUag7dE68YQUf6Udc51zETQEYloBMSZm10y6TE00mrIvqRV6';
  const sercreteKey='sk_test_51H0ir0AyvULANtQRw9JfJ1TrcPPMa0MljRKTQP5v51jGGQc5pND78ScdQH40HU9Ile2sBnjuBIZOfp669Xv7BRv500DXiQsk3Y';

  const onToken = token => {
    console.log(token);
    alert('Payment Succesful!');
  };

  return (
    <StripeCheckout
      label='Pay Now'
      name='CRWN Clothing Ltd.'
      billingAddress
      shippingAddress
      image='https://svgshare.com/i/CUz.svg'
      description={`Your total is $${price}`}
      amount={priceForStripe}
      panelLabel='Pay Now'
      token={onToken}
      stripeKey={publishableKey}
    />
  );
};

export default StripeCheckoutButton;
